import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Home from './components/Home';
import Login from './components/Login';
import Navbar from './components/Navbar';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  const handleLogin = () => {
    setIsAuthenticated(true);
  };

  const handleLogout = () => {
    setIsAuthenticated(false);
  };

  return (
      <Router>
        <div>
          {/* Pokazuj navbar tylko, gdy użytkownik jest zalogowany */}
          {isAuthenticated && <Navbar onLogout={handleLogout} />}
          <Routes>
            {/* Przekierowanie na stronę logowania, jeśli nie jest zalogowany */}
            <Route path="/login" element={isAuthenticated ? <Navigate to="/home" /> : <Login onLogin={handleLogin} />} />
            {/* Strona główna dostępna tylko, gdy użytkownik jest zalogowany */}
            <Route path="/home" element={isAuthenticated ? <Home /> : <Navigate to="/login" />} />
            {/* Domyślne przekierowanie na stronę logowania */}
            <Route path="/" element={<Navigate to={isAuthenticated ? "/home" : "/login"} />} />
          </Routes>
        </div>
      </Router>
  );
}

export default App;
